<div class="wrap">	<?php html_head('Sidebar Profile'); ?>	<?php settings_errors(); ?>	<?php	$picture = esc_attr( get_option( 'profile_picture' ) );	$firstName = esc_attr( get_option( 'getweb_first_name' ) );	$lastName = esc_attr( get_option( 'getweb_last_name' ) );	$fullName = $firstName . ' ' . $lastName;	$description = esc_attr( get_option( 'getweb_profile_desc' ) );	$twitter_icon = esc_attr( get_option( 'getweb_profile_twitter' ) );	$facebook_icon = esc_attr( get_option( 'getweb_profile_fb' ) );	$gplus_icon = esc_attr( get_option( 'getweb_profile_gplus' ) );	?>	<div class="col-lg-6">    <form id="submitForm" method="post" action="options.php" class="getweb-general-form">			<?php			settings_fields( 'getweb_plugin_sidebar_settings' );			do_settings_sections( 'getweb_sidebar' );			getweb_submit_button();			?>		</form>	</div>	<div class="col-lg-6">		<div class="getweb-sidebar-preview">			<div class="getweb-sidebar">				<div class="image-container">					<div id="profile-picture-preview" class="profile-picture" style="background-image: url(<?php print $picture; ?>);"></div>				</div>				<h1 class="getweb-username"><?php echo $fullName; ?></h1>				<h2 class="getweb-description"><?php print $description; ?></h2>				<div class="icons-wrapper">					<?php if( !empty( $twitter_icon ) ): ?>						<span class="getweb-icon-sidebar dashicons-before dashicons-twitter"></span>					<?php endif;					if( !empty( $gplus_icon ) ): ?>						<span class="getweb-icon-sidebar getweb-icon-sidebar--gplus dashicons-before dashicons-googleplus"></span>					<?php endif;					if( !empty( $facebook_icon ) ): ?>						<span class="getweb-icon-sidebar dashicons-before dashicons-facebook-alt"></span>					<?php endif; ?>				</div>			</div>		</div>	</div>	<?php html_footer(); ?></div>